using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using Jlw.Standard.Utilities.Data;
using SixLabors.ImageSharp;
using SixLabors.ImageSharp.PixelFormats;

namespace Jlw.Utilities.OpenMaps
{
    public class MapMarker
    {
        public double X { get; protected set; }
        public double Y { get; protected set; }
        public string MarkerId { get; protected set; }
        public Image<Rgba32> ImageData { get; protected set; }
        public Image<Rgba32> ShadowData { get; protected set; }
        protected static Regex rxMarkers = new Regex(@"^\s*(-?[0-9]+[\\.]?[0-9]*)\s*,\s*(-?[0-9]+[\\.]?[0-9]*)\s*[,]?([a-z0-9\\-]*)", RegexOptions.IgnorePatternWhitespace | RegexOptions.IgnoreCase);

        private static readonly Dictionary<string, string> ImageDataList = new Dictionary<string, string>()
        {
            { "fa-marker-shadow", "" },
            { "fa-marker-red", "" }
        };

        public MapMarker(string marker)
        {
            if (rxMarkers.IsMatch(marker))
            {
                var matches = rxMarkers.Match(marker);
                Y = DataUtility.ParseFloat(matches.Groups[1].Value);
                X = DataUtility.ParseFloat(matches.Groups[2].Value);
                MarkerId = matches.Groups[3].Value;
                SetMarkerImage(MarkerId);
            }


        }

        internal void Initialize(int x, int y, string markerId)
        {

        }

        protected void SetMarkerImage(string markerId)
        {
            string base64 = ImageDataList.FirstOrDefault(kvp=>kvp.Key.Equals(markerId, StringComparison.InvariantCultureIgnoreCase)).Value;
            ImageData = null;

            if (string.IsNullOrWhiteSpace(base64))
                return;

            ImageData = Image.Load(Convert.FromBase64String(base64));
            ShadowData = Image.Load(Convert.FromBase64String(ImageDataList.FirstOrDefault(kvp=>kvp.Key.Equals("fa-marker-shadow", StringComparison.InvariantCultureIgnoreCase)).Value));
        }

    }
}